package Programs;

public class ifelseone {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          
	int num=200;
		
		if(num==20)
		{
			System.out.println("Number is 20 ");
		}
		
		else if(num==30)
		{
			System.out.println("Number is 30");
		}
		else if(num==40)
		{
			System.out.println("number is 40");
		}
		else if(num==200)
		{
			System.out.println("Number is 200");
		}
		
		else
		{
			System.out.println(" None of the numbers are matching");
		}
	}

}
